import React from "react";
import { View, Text, StyleSheet } from "react-native";
import { Icon } from "react-native-elements";

const WinnerPanel = ({ winnerArray }) => {
  return (
    <View style={styles.panel}>
      <View style={styles.person}>
        <Icon name="person" color="#a487c3" size="40px" />
        <Text style={styles.shift}>1st Shift</Text>
        <Text style={styles.winner}>{winnerArray[0]}</Text>
      </View>

      <View style={styles.person}>
        <Icon name="person" color="#a487c3" size="40px" />
        <Text style={styles.shift}>2nd Shift</Text>
        <Text style={styles.winner}>{winnerArray[1]}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  panel: {
    position: "absolute",
    top: 100,
    padding: 15,
    // marginTop: -80,
    // borderRadius: 10,
    flexDirection: "row",
    alignItems: "center",
    // width: 300,
    // height: 100,
    // zIndex: -2,
  },
  shift: {
    color: "gray",
    fontSize: 15,
    textAlign: "center",
  },
  person: {
    justifyContent: "center",
    alignItems: "center",
    width: 150,
    // height: 100,
  },
  winner: {
    // height: 30,
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 20,
    color: "#FFF",
    // width: 150,
    // flex: 1,
    flexWrap: "wrap",
    alignItems: "flex-start",
  },
});

export default WinnerPanel;
