import React from "react";
import { View, Text, StyleSheet, TouchableOpacity } from "react-native";
import { Icon } from "react-native-elements";
// import { BsFillPersonFill, BsFillPersonPlusFill } from "react-icons/bs";

const MenuBar = ({
  participants,
  toggleAddParticipantInput,
  toggleParticipantList,
}) => {
  return (
    <View style={styles.menu}>
      <TouchableOpacity onPress={() => toggleAddParticipantInput()}>
        <View style={styles.menuItem}>
          {/* <BsFillPersonPlusFill size={25} /> */}
          <Icon name="person-add-alt-1" color="#a487c3" size="25px" />
        </View>
      </TouchableOpacity>

      <TouchableOpacity onPress={() => toggleParticipantList()}>
        <View style={styles.menuItem}>
          <Text style={styles.text}>{participants}</Text>
          {/* <BsFillPersonFill size={25} /> */}
          <Icon name="person" color="#a487c3" size="25px" />
        </View>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  menu: {
    position: "absolute",
    top: 0,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    width: "100%",
    paddingVertical: 20,
    paddingHorizontal: 30,
  },
  menuItem: {
    flexDirection: "row",
    alignItems: "center",
    width: 40,
  },
  text: {
    fontWeight: "bold",
    color: "#a487c3",
  },
});

export default MenuBar;
